package ch06.external;

public class GarageDoor {

	public void up(){
		System.out.println("차고문이 열립니다.");
	}

	public void down(){
		System.out.println("차고문이 닫힙니다.");
	}

	public void	stop(){
		System.out.println("차고문이 동작을 멈춥니다.");
	}

	public void lightOn(){
		System.out.println("차고에 불이 켜집니다.");
	}

	public void	lightOff(){
		System.out.println("차고에 불이 꺼집니다.");
	}
}
